import './App.css';
import { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt, faSearch, faCloudSun, faPlusCircle } from '@fortawesome/free-solid-svg-icons'
import AirQuality from './components/AirQuality';
import Feels from './components/Feels';

function App() {

  const [data, setData] = useState(null);
  const [city, setCity] = useState("Suceava");
  const [search, setSearch] = useState(false);
  const [favorite, setFavorite] = useState('');
  const [info, setInfo] = useState(false);
  const [infoFeels, setInfoFeels] = useState(false);

  const day = ['SUNDAY', 'MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY', 'FRIDAY', 'SATURDAY'];
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const [click, setClick] = useState(false)

  const submitHandler = (e) => {
    if (e.key === 'Enter'){
      setClick(true)
      console.log(click)
    } 
  }  

  useEffect(() => {
    fetch('http://api.weatherapi.com/v1/forecast.json?key=fff7d45bd3bf4a2fbfd135016210811&q=' + city + '&days=10&aqi=yes&alerts=yes')
      .then(res => res.json())
      .then(res => {
        setData(res)
        console.log(res);
        setClick(false);
      });
  },[ click === true ])

    useEffect(() =>{
      fetch('http://localhost:8000/data')
      .then(res => res.json())
      .then(res => {
        setFavorite(res);
        console.log(res);
      });
    }, [])

  const day_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var weekday = date.getDay();
      return (
        day[weekday]
      )
    }
  }

  const month_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var month = date.getMonth();
      return (
        months[month]
      )
    }
  }

  const clickHandler = () => {
    if(search){
      setSearch(false)
    } else {
      setSearch(true)
    }
  }

  const favoriteHandler = (e) => {
    e.preventDefault();
    fetch('http://localhost:8000/data', {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({title: city})
    }).then(()=>{
      console.log(city);
      console.log('Successfully added!');
    })
  }

  const handleFav  = (title) => {
    setCity(title);
    setClick(true)
  }

  const DeleteHandler = (id) => {
    fetch('http://localhost:8000/data/' + id, {
      method: 'DELETE'
    }).then(() => {
      console.log('Successfully deleted!')
    })
  }

  const infoHendler = () => {
    if(info === false){
      setInfo(true)
    }else{
      setInfo(false)
    }
  }

  const infoHendlerFeels = () => {
    if(infoFeels === false){
      setInfoFeels(true)
    }else{
      setInfoFeels(false)
    }
  }

  return (
    <div className="App">

        <div className='weather-box'>
            {data && 
            <div className="grid-box">
              <p className="grid-box1">{data.current.temp_c}°</p>
              <div className='grid-22'>
                <p className="grid-box2">{data.location.name}</p>
                <p className="grid-box4">10:36 - {day_w()}, 22 Oct </p>
              </div>
              <div className='grid-222'>
                <p className="grid-box5"><FontAwesomeIcon icon={faCloudSun} size="2x"/></p>
                <p className="grid-box3">{data.current.condition.text}</p>
              </div>
            </div>
            }
        </div>

        <div className='component'>
          <div className='serch-box'>
            { search && <input
            className="search-bar"
            type="text"
            onChange={(e) => setCity(e.target.value)}
            value={city}  
            placeholder="Cautare.."
            onKeyPress={submitHandler}
            />}
            <div className='search-icon' onClick={clickHandler}>
              <FontAwesomeIcon icon={faSearch} size="2x"/>
            </div>
          </div>

          <div className='fav-p'>
            <p>Favorite location</p>
            <FontAwesomeIcon icon={faPlusCircle} size="2x"/>
            <button className='button' onClick={favoriteHandler}>Add</button>
          </div>

          <div className='fav' >
              {favorite && favorite.map((fav) => (
                <div className='fav-map' key={fav.id} onClick={() => handleFav(fav.title)}>
                  {fav.title}
                  <span className='delete-icon' onClick={() => {DeleteHandler(fav.id)}}>
                    <FontAwesomeIcon icon={faTrashAlt} size="1x"/>
                  </span>
                </div>
              ))}
          </div>

          <div className='fav-pp'>
            <p>Weather details</p>
          </div>

          <div className='fav' >
            <div className='details'>
              <p>Humidity</p>
              {data && <p>{data.current.humidity}%</p>}
            </div>
            <div className='wind'>
              <p>Wind</p>
              {data && <p>{data.current.wind_mph} km/h</p>}
            </div>
            <div className='wind' onMouseEnter={infoHendlerFeels} onMouseLeave={infoHendlerFeels}>
              <p>Feels like</p>
              {data && <p>{data.current.feelslike_c}°</p>}
            </div>
            <div className='wind' onMouseEnter={infoHendler} onMouseLeave={infoHendler}>
              <p>Air qualiti</p>
              {data && <p>{data.current.air_quality.no2.toFixed(2)} no2</p>}
            </div>
          </div>

          {info && <div className='fav' >
            <AirQuality no2 = {data.current.air_quality.no2} />
          </div>}

          {infoFeels && <div className='fav' >
            <Feels temp_c = {data.current.temp_c}/>
          </div>}

        </div>
    </div>
  );
}

export default App;
