      .then(res => res.json())
      .then(res => {
        setData(res)
        console.log(res);
      });
    }
  }  

  const day_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var weekday = date.getDay();
      return (
        day[weekday]
      )
    }
  }

  const month_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var month = date.getMonth();
      return (
        months[month]
      )
    }
  }

  const clickHandler = () => {
    if(search){
      setSearch(false)
    } else {
      setSearch(true)
    }
  }

