import './App.css';
import { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrashAlt, faSearch } from '@fortawesome/free-solid-svg-icons'

function App() {

  const [data, setData] = useState(null);
  const [city, setCity] = useState("Suceava");
  const [search, setSearch] = useState(false);
  const [favorite, setFavorite] = useState('')

  const day = ['SUNDAY', 'MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY', 'FRIDAY', 'SATURDAY'];
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  const [click, setClick] = useState(false)

  const submitHandler = (e) => {
    if (e.key === 'Enter'){
      setClick(true)
      console.log(click)
    } 
  }  

  useEffect(() => {
    fetch('http://api.weatherapi.com/v1/forecast.json?key=fff7d45bd3bf4a2fbfd135016210811&q=' + city + '&days=10&aqi=yes&alerts=yes')
      .then(res => res.json())
      .then(res => {
        setData(res)
        console.log(res);
        setClick(false);
      });
  },[ click === true ])

    useEffect(() =>{
      fetch('http://localhost:8000/data')
      .then(res => res.json())
      .then(res => {
        setFavorite(res);
        console.log(res);
      });
    }, [])

  const day_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var weekday = date.getDay();
      return (
        day[weekday]
      )
    }
  }

  const month_w = () => {
    if(data){
      var date = new Date(data.forecast.forecastday[0].date);
      var month = date.getMonth();
      return (
        months[month]
      )
    }
  }

  const clickHandler = () => {
    if(search){
      setSearch(false)
    } else {
      setSearch(true)
    }
  }

  const favoriteHandler = (e) => {
    e.preventDefault();
    fetch('http://localhost:8000/data', {
      method: 'POST',
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({title: city})
    }).then(()=>{
      console.log(city);
      console.log('Successfully added!');
    })
  }

  const handleFav  = (title) => {
    setCity(title);
    setClick(true)
  }

  const DeleteHandler = (id) => {
    fetch('http://localhost:8000/data/' + id, {
      method: 'DELETE'
    }).then(() => {
      console.log('Successfully deleted!')
    })
  }

  return (
    <div className="App">

        <div className='weather-box'>
            {data && 
            <div className="content-w">
              <p className="temp">{data.current.temp_c}°</p>
              <p className="location">{data.location.name}</p>
              <p className="condition">{data.current.condition.text}</p>
              <p>{data.current.last_updated}</p>
              <p>{day_w()}</p>
              <p>{month_w()}</p>
            </div>
            }
        </div>

        <div className='component'>
          <div className='serch-box'>
            { search && <input
            className="search-bar"
            type="text"
            onChange={(e) => setCity(e.target.value)}
            value={city}  
            placeholder="Cautare.."
            onKeyPress={submitHandler}
            />}
            <div className='search-icon' onClick={clickHandler}>
              <FontAwesomeIcon icon={faSearch} size="2x"/>
            </div>
          </div>

          <div className='fav-p'>
            <p>Favorite location</p>
            <button className='button' onClick={favoriteHandler}>Add</button>
          </div>

          <div className='fav' >
              {favorite && favorite.map((fav) => (
                <div className='fav-map' key={fav.id} onClick={() => handleFav(fav.title)}>
                  {fav.title}
                  <span className='delete-icon' onClick={() => {DeleteHandler(fav.id)}}>
                    <FontAwesomeIcon icon={faTrashAlt} size="1x"/>
                  </span>
                </div>
              ))}
          </div>

          <div className='fav-pp'>
            <p>Weather details</p>
          </div>

          <div className='fav' >
            <div className='details'>
              <p>Humidity</p>
              {data && <p>{data.current.humidity}%</p>}
            </div>
            <div className='wind'>
              <p>Wind</p>
              {data && <p>{data.current.wind_mph} km/h</p>}
            </div>
            <div className='wind'>
              <p>Feels like</p>
              {data && <p>{data.current.feelslike_c}°</p>}
            </div>
          </div>

        </div>
    </div>
  );
}

export default App;
