import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCloudSun } from '@fortawesome/free-solid-svg-icons'
import './DayWeather.css'

const DayWeather = ({data}) => {

    const day = ['SUNDAY', 'MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY', 'FRIDAY', 'SATURDAY'];

    const day_w = () => {
        if(data){
          var date = new Date(data.forecast.forecastday[0].date);
          var weekday = date.getDay();
          return (
            day[weekday]
          )
        }
      }

    return (
        <div className='weather-box'>
            {data && 
            <div className="box">
              <p className="box-date">{data.location.name}</p>
              <p className="box-2">{day_w()}, 22 Octomber 12:20</p>
              <div className='box-temp'>
                <p className='icon-temp'><FontAwesomeIcon icon={faCloudSun} size="2x"/></p>
                <p className='temp-principal'>{data.current.temp_c}Â°</p>
              </div>

                <div className='box-3'>

                  <p>{data.current.condition.text}</p>
                </div>
            </div>
            }
        </div>
    )
}

export default DayWeather
