import './App.css';
import { useState } from 'react'

function App() {

  const [data, setData] = useState(null);
  const [city, setCity] = useState("");

  const day = ['SUNDAY', 'MONDAY', 'TUESDAY', 'WEDNESDAY', 'THURSDAY', 'FRIDAY', 'SATURDAY'];
  var date = new Date(data.forecast.forecastday[0].date);
  var weekday = date.getDay();

  const submitHandler = (e) => {

    if (e.key === 'Enter'){
      fetch('http://api.weatherapi.com/v1/forecast.json?key=fff7d45bd3bf4a2fbfd135016210811&q=' + city + '&days=10&aqi=yes&alerts=yes')
      .then(res => res.json())
      .then(res => {
        setData(res)
        console.log(res);
      });
    }
  }  

  return (
    <div className="App">

        <div className='weather-box'>
            Weather
            {data && <p className="location">{data.location.name}</p>}
            {data && <p className="temp">{data.current.temp_c}Â°</p>}
            {data && <p className="condition">{data.current.condition.text}</p>}
            {data }
        </div>


        <div className='component'>
          Component
          <div className='serch-box'>
            <input
            className="search-bar"
            type="text"
            onChange={(e) => setCity(e.target.value)}
            value={city}  
            placeholder="Cautare.."
            onKeyPress={submitHandler}
            />
          </div>
        </div>
    </div>
  );
}

export default App;
